import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
// import Jumbotron from "react-bootstrap/Jumbotron";
import "./myFavoriteBooks.css";
import axios from "axios";
// import { useAuth0 } from "@auth0/auth0-react";
import { withAuth0 } from "@auth0/auth0-react";
import Carousel from "react-bootstrap/Carousel";
require("dotenv").config();

class MyFavoriteBooks extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      // showComponent: false,
    };
  }
 componentDidMount = async () =>{
    let server = process.env.REACT_APP_SERVER_URL;
    let bookReqUrl = `${server}/books?email=${this.props.auth0.user.email}`;
    const resultsBook = await axios.get(bookReqUrl);
    this.setState({
      books: resultsBook.data[0].books,
      // showComponent:true,
    });
    console.log(this.state.books.books[0]);
  }
  render() {
    // const { isAuthenticated } = this.props.auth0;
    return (
      <>
       {/* {isAuthenticated && this.componentDidMount()} */}
        {/* <Jumbotron> */}
          <h1>My Favorite Books</h1>
          <p>This is a collection of my favorite books</p>
          {/* </Jumbotron> */}
          <Carousel >
      {/* { this.state.books.length !==0 && */}
        {this.state.books.books.map((item,indx) => {
          return (
              <Carousel.Item key={indx} interval={1000}>
                <img className="d-block w-100" src={item.status} alt="slide" />
                <Carousel.Caption>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                </Carousel.Caption>
              </Carousel.Item>
          );
        })}}
                    </Carousel>
      </>
    );
  }
}

export default withAuth0(MyFavoriteBooks);
