import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export class BookFormModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
          showModal: false,
        };

    updateModal=()=>{
        this.setState({
          showModal:true,
        });
      }
    }
  render() {
    return (
      <div>

        <button type="submit" onClick={this.props.updatBook}>
          Add Book
        </button>
        <Modal show={this.props.flag} onHide={""}>
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              {this.props.title1}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Label>Book Name</Form.Label>
                <Form.Control type="text" placeholder="text Book" />
                <Form.Text className="text-muted"></Form.Text>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicDescription">
                <Form.Label>description</Form.Label>
                <Form.Control type="text" placeholder="description book" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicStatus">
                <Form.Label>Status</Form.Label>
                <Form.Control type="text" placeholder="status book" />
                </Form.Group>
                <Form.Group  className="mb-3" controlId="formBasicStatus">
                  <Form.Label>Status</Form.Label>
                  <Form.Select defaultValue="Choose...">
                    <option>Choose...</option>
                    <option>...</option>
                    <option>...</option>
                    <option>...</option>
                  </Form.Select>
              </Form.Group>
              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            {/* <Button onClick={this.close}>Close</Button> */}
         </Modal.Footer>
        </Modal>  
      </div>
    );
  }
}
export default BookFormModal;
