    console.log(idx);
    this.show();
   this.setState({
     name:item.name,
     description:item.description,
     item:item,

   })



  }
  // setName = (e) => {
  //   this.setState({
  //    name: e.target.value
  //   })
  // }

  // setDescription = (e) => {
  //   this.setState({
  //     description: e.target.value
  //   })
  // }
  // setStatus = (e) => {
  //   this.setState({
  //     status: e.target.value
  //   })
  // }
  updateBook = async(e) => {
    e.preventDefault();
    const bookData = {
      name: e.target.name.value,
      description: e.target.description.value,
      status:e.target.status.value,
      email:this.props.auth0.user.email
    }
